import React, { useState } from "react";

function UploadScreen({ users = [], onSubmit }) {
  const [file, setFile] = useState(null);
  const [selectedUsers, setSelectedUsers] = useState([]);

  const handleUserToggle = (id) => {
    setSelectedUsers((prev) =>
      prev.includes(id) ? prev.filter((u) => u !== id) : [...prev, id]
    );
  };

  const handleSubmit = () => {
    if (!file) {
      alert("Please select a file first.");
      return;
    }
    if (selectedUsers.length === 0) {
      alert("Please select at least one user.");
      return;
    }
    onSubmit(file, selectedUsers);
  };

  return (
    <div className="p-6 bg-white shadow-md rounded-lg max-w-lg mx-auto">
      <h2 className="text-xl font-semibold mb-4">Upload Report</h2>

      {/* File upload */}
      <input
        type="file"
        accept=".xlsx,.csv"
        onChange={(e) => setFile(e.target.files[0])}
        className="mb-4"
      />

      {/* User checkboxes */}
      <h3 className="font-medium mb-2">Assign Users</h3>
      <div className="grid grid-cols-2 gap-2 mb-4">
        {users.map((user) => (
          <label key={user.id} className="flex items-center space-x-2">
            <input
              type="checkbox"
              checked={selectedUsers.includes(user.id)}
              onChange={() => handleUserToggle(user.id)}
            />
            <span>{user.name}</span>
          </label>
        ))}
      </div>

      {/* Submit button */}
      <button
        onClick={handleSubmit}
        className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
      >
        Submit
      </button>
    </div>
  );
}

export default UploadScreen;
