// src/pdfUtils.js
import jsPDF from "jspdf";
import "jspdf-autotable";

/**
 * Generates the "Counts Needed" PDF in two columns.
 * Fill order: top→bottom in left column, then top→bottom in right column.
 * - Monochrome (laser printer friendly)
 * - Font size 8
 * - Page number in header & footer
 * - "Count" cells are empty boxes (grid borders only)
 */
export function generateMissingCountsPDF(missing = []) {
  const doc = new jsPDF({ unit: "mm", format: "letter", orientation: "portrait" });

  const margin = { top: 15, right: 12, bottom: 15, left: 12 };
  const pageWidth = doc.internal.pageSize.getWidth();
  const pageHeight = doc.internal.pageSize.getHeight();
  const usableW = pageWidth - margin.left - margin.right;
  const colW = usableW / 4; // 4 table columns: SKU | Count | SKU | Count

  // Flatten SKUs
  const skus = (missing || [])
    .map((e) => (e && e.sku != null ? String(e.sku) : ""))
    .filter((s) => s !== "");
  const half = Math.ceil(skus.length / 2);
  const left = skus.slice(0, half);
  const right = skus.slice(half);

  // Combine rows into table body
  const body = [];
  for (let i = 0; i < half; i++) {
    body.push([left[i] ?? "", "", right[i] ?? "", ""]);
  }

  // Page number placeholder
  const totalPagesExp = "{total_pages_count_string}";

  doc.autoTable({
    head: [["SKU", "Count", "SKU", "Count"]],
    body,
    startY: margin.top + 6,
    theme: "grid",
    margin,
    styles: {
      halign: "center",
      valign: "middle",
      fontSize: 8,           // ✅ smaller text
      lineWidth: 0.3,
      cellPadding: 2,
      textColor: [0, 0, 0],  // monochrome
    },
    headStyles: {
      halign: "center",
      valign: "middle",
      fontSize: 8,
      textColor: [0, 0, 0],
      fillColor: [255, 255, 255], // white header background
      lineWidth: 0.4,
    },
    columnStyles: {
      0: { cellWidth: colW },
      1: { cellWidth: colW },
      2: { cellWidth: colW },
      3: { cellWidth: colW },
    },
    didDrawPage: (data) => {
      const pageStr = `Page ${data.pageNumber} of ${totalPagesExp}`;
      doc.setFontSize(8);
      doc.text(pageStr, pageWidth / 2, 10, { align: "center" });
      doc.text(pageStr, pageWidth / 2, pageHeight - 5, { align: "center" });
    },
  });

  doc.putTotalPages(totalPagesExp);

  doc.save(`Counts_Needed_${Date.now()}.pdf`);
}
