import jsPDF from "jspdf";
import "jspdf-autotable";

/**
 * Generate a "Counts Needed" PDF
 * Two SKU+Count pairs per row, full page width, compact formatting
 */
export const generateMissingCountsPDF = (missingRows) => {
  const doc = new jsPDF({ orientation: "portrait", unit: "pt", format: "letter" });

  doc.setFont("helvetica", "normal");
  doc.setFontSize(14);
  doc.text("Counts Needed", 40, 40);

  // Build rows: [SKU, blank, SKU, blank]
  const rows = [];
  for (let i = 0; i < missingRows.length; i += 2) {
    const leftSKU = missingRows[i]?.sku || "";
    const rightSKU = missingRows[i + 1]?.sku || "";
    rows.push([leftSKU, "__________", rightSKU, "__________"]);
  }

  doc.autoTable({
    startY: 60,
    head: [["SKU", "Count", "SKU", "Count"]],
    body: rows,
    styles: {
      fontSize: 10,
      cellPadding: 4,
      valign: "middle",
      halign: "left",
    },
    headStyles: {
      fillColor: [41, 128, 185],
      textColor: 255,
      fontSize: 11,
    },
    columnStyles: {
      0: { cellWidth: 200 }, // SKU left
      1: { cellWidth: 80, halign: "center" }, // Count left
      2: { cellWidth: 200 }, // SKU right
      3: { cellWidth: 80, halign: "center" }, // Count right
    },
    margin: { left: 40, right: 40 },
    tableWidth: "auto",
    tableLineColor: [220, 220, 220],
    tableLineWidth: 0.5,
  });

  doc.save("counts-needed.pdf");
};
