import React, { useState, useEffect } from "react";
import { createRoot } from "react-dom/client";
import { supabase } from "./supabase";
import InventoryApp from "./App";

function Main() {
  const [session, setSession] = useState(null);
  const [username, setUsername] = useState("");
  const [role, setRole] = useState("user");

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      if (session) setSession(session.user.user_metadata);
    });

    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setSession(session ? session.user.user_metadata : null);
    });

    return () => listener?.subscription.unsubscribe();
  }, []);

  const handleLogin = () => {
    if (username && role) {
      setSession({ username, role });
    }
  };

  const handleLogout = () => {
    setSession(null);
    setUsername("");
    setRole("user");
  };

  if (!session) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="p-6 border rounded shadow w-96 space-y-4">
          <h1 className="text-2xl font-bold mb-2">Login</h1>
          <input
            type="text"
            className="w-full border px-3 py-2 rounded"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <select
            className="w-full border px-3 py-2 rounded"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="user">User</option>
            <option value="admin">Admin</option>
          </select>
          <button
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
            onClick={handleLogin}
          >
            Login
          </button>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="flex justify-end p-4">
        <button
          className="text-sm text-blue-500 hover:underline"
          onClick={handleLogout}
        >
          Logout ({session.username})
        </button>
      </div>
      <InventoryApp session={session} />
    </>
  );
}

const container = document.getElementById("root");
const root = createRoot(container);
root.render(<Main />);
