import jsPDF from "jspdf";
import "jspdf-autotable";

/**
 * Generate a "Counts Needed" PDF
 * @param {Array} missingRows - Array of { sku, description }
 */
export const generateMissingCountsPDF = (missingRows) => {
  const doc = new jsPDF({ orientation: "portrait", unit: "pt", format: "letter" });

  doc.setFont("helvetica", "normal");
  doc.setFontSize(12);

  // Build rows in 2-column format
  const tableData = [];
  for (let i = 0; i < missingRows.length; i += 2) {
    tableData.push([
      `${missingRows[i]?.sku || ""} - ${missingRows[i]?.description || ""}`,
      `${missingRows[i + 1]?.sku || ""} - ${missingRows[i + 1]?.description || ""}`,
    ]);
  }

  doc.text("Counts Needed Report", 40, 40);

  doc.autoTable({
    startY: 60,
    head: [["Item 1", "Item 2"]],
    body: tableData,
    styles: { fontSize: 12, cellPadding: 6 },
    headStyles: { fillColor: [41, 128, 185], textColor: 255 },
    tableLineColor: [200, 200, 200],
    tableLineWidth: 0.5,
  });

  doc.save("counts-needed.pdf");
};
