import React, { useState, useEffect, useRef } from "react";
import * as XLSX from "xlsx";
import { supabase } from "./supabase";
import UploadScreen from "./UploadScreen";
import { generateMissingCountsPDF } from "./pdfUtils";

const Input = React.forwardRef((props, ref) => (
  <input
    {...props}
    ref={ref}
    className={`px-2 py-1 rounded border ${props.className || "border-gray-300"}`}
  />
));

const Button = (props) => (
  <button
    {...props}
    className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
  />
);

export default function InventoryApp({ session }) {
  const { username, role } = session;
  const [userRole] = useState(role);
  const [currentUser] = useState(username);
  const [entries, setEntries] = useState([]);
  const [fileId, setFileId] = useState(null);
  const [reportList, setReportList] = useState([]);
  const [userList, setUserList] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const inputRefs = useRef([]);

  const isAdmin = (userRole || "").toLowerCase().includes("admin");

  // Debug role once per mount
  useEffect(() => {
    console.log("Session role:", role);
    console.log("App state userRole:", userRole);
  }, [role, userRole]);

  useEffect(() => {
    loadReportList();
    loadUserList();
  }, []);

  const loadReportList = async () => {
    const { data, error } = await supabase
      .from("files")
      .select("id, created_at")
      .order("created_at", { ascending: false });
    if (!error) setReportList(data || []);
  };

  const loadUserList = async () => {
    const { data, error } = await supabase.from("users").select("username");
    if (!error) setUserList((data || []).map((u) => u.username));
  };

  // Upload + clean + assign evenly (preserving original order)
  const handleUploadSubmit = async (file, activeUsers) => {
    if (!file) {
      alert("Please select a file.");
      return;
    }
    if (!activeUsers || activeUsers.length === 0) {
      alert("Please select at least one user.");
      return;
    }
    setSelectedUsers(activeUsers);

    const reader = new FileReader();
    reader.onload = async (evt) => {
      const u8 = new Uint8Array(evt.target.result);
      // Universal loader: CSV/XLS/XLSX/XML
      const workbook = XLSX.read(u8, { type: "array" });
      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];

      // Read as a 2D array
      let data2d = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

      // --- CLEANING ---
      // 1) Delete rows 1–5
      data2d = data2d.slice(5);

      if (data2d.length === 0) {
        alert("No rows found after cleaning.");
        return;
      }

      // 2) Set USER header at E1 (index 4)
      if (!data2d[0]) data2d[0] = [];
      data2d[0][4] = "USER";

      // 3) Remove entire rows with blank Column B (index 1), but keep header
      data2d = data2d.filter((row, idx) => {
        if (idx === 0) return true; // keep header
        const colB = row?.[1];
        return colB !== undefined && colB !== null && String(colB).trim() !== "";
      });

      // Data rows (skip header row)
      const rows = data2d.slice(1);

      if (rows.length === 0) {
        alert("No valid data rows after cleaning.");
        return;
      }

      // Create a new file record
      const { data: fileInsert, error: fileErr } = await supabase
        .from("files")
        .insert([{ uploaded_by: currentUser }])
        .select()
        .single();

      if (fileErr || !fileInsert) {
        alert("File upload error: " + (fileErr?.message || "unknown error"));
        return;
      }

      const newFileId = fileInsert.id;
      setFileId(newFileId);

      // Split evenly by contiguous blocks, preserving order
      const chunkSize = Math.ceil(rows.length / activeUsers.length);
      const assignedEntries = rows.map((row, index) => {
        const userIndex = Math.floor(index / chunkSize);
        return {
          file_id: newFileId,
          upload_index: index,
          sku: row?.[0] ?? "",                 // Column A  (SKU)
          on_hand: row?.[1] != null ? parseInt(row[1], 10) : null, // Column B (On Hand)
          description: row?.[3] ? String(row[3]) : "", // Column D
          assigned_to: activeUsers[userIndex] || activeUsers[activeUsers.length - 1],
        };
      });

      const { error: insertErr } = await supabase
        .from("entries")
        .insert(assignedEntries);
      if (insertErr) {
        alert("Upload failed: " + insertErr.message);
        return;
      }

      loadEntries(newFileId);
    };

    // Universal read mode for all Excel flavors
    reader.readAsArrayBuffer(file);
  };

  const loadEntries = async (id) => {
    const allData = [];
    const chunkSize = 1000;
    let from = 0;
    let to = chunkSize - 1;
    let keepGoing = true;

    while (keepGoing) {
      let query = supabase
        .from("entries")
        .select(
          "id, file_id, upload_index, sku, on_hand, description, count, assigned_to",
          { count: "exact" }
        )
        .order("upload_index", { ascending: true })
        .range(from, to);

      if (isAdmin) {
        query = query.eq("file_id", id);
      } else {
        query = query.eq("file_id", id).eq("assigned_to", currentUser);
      }

      const { data, error } = await query;

      console.log("Sample entries returned:", data?.slice(0, 5));


      if (error) {
        console.error("Error loading entries:", error);
        break;
      }

      if (data && data.length > 0) {
        allData.push(...data);
      }

      if (!data || data.length < chunkSize) {
        keepGoing = false;
      } else {
        from += chunkSize;
        to += chunkSize;
      }
    }

    // small debug sample so console stays readable
    console.log(
      "Loaded sample entries:",
      allData.slice(0, 10).map((e) => ({ sku: e.sku, assigned_to: e.assigned_to }))
    );

    setEntries(allData);
    setFileId(id);
  };

  const handleInputChange = async (entryId, value, index) => {
    const newEntries = [...entries];
    const entry = newEntries.find((e) => e.id === entryId);
    entry.count = value === "" ? null : parseInt(value, 10);
    entry.entered_by = currentUser;
    setEntries(newEntries);

    const { error } = await supabase
      .from("entries")
      .update({ count: entry.count, entered_by: currentUser })
      .eq("id", entryId);

    if (error) {
      console.error("Error saving count:", error);
      alert("Failed to save count. See console for details.");
    }
  };

  const getInputClass = (count, onHand) => {
    if (count === undefined || count === null || count === "") return "border-gray-300";
    const diff = Math.abs(count - (onHand ?? 0));
    if (diff === 0) return "border-green-500";
    if (diff <= 10) return "border-yellow-400";
    if (diff <= 20) return "border-orange-400";
    return "border-red-500";
  };

  const handleGenerateMismatchReport = () => {
    const mismatched = entries.filter(
      (e) => e.count !== undefined && e.count !== null && e.count !== e.on_hand
    );
    const csvRows = ["SKU,On Hand,Count,Difference,User"];
    mismatched.forEach((e) => {
      const diff = (e.count ?? 0) - (e.on_hand ?? 0);
      csvRows.push(`${e.sku},${e.on_hand ?? ""},${e.count ?? ""},${diff},${e.assigned_to ?? ""}`);
    });
    const blob = new Blob([csvRows.join("\n")], { type: "text/csv" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `Mismatch_Report_${Date.now()}.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleDownloadMissingCounts = () => {
    const missing = entries.filter((e) => e.count === null || e.count === undefined);
    generateMissingCountsPDF(missing);
  };

  const focusNextEditableInput = (startIndex) => {
    for (let i = startIndex + 1; i < entries.length; i++) {
      if (entries[i]?.assigned_to === currentUser) {
        const next = inputRefs.current[i];
        if (next) {
          next.focus();
          break;
        }
      }
    }
  };

  return (
    <div className="p-6 w-full space-y-4">
      <div className="flex justify-between">
        <div className="text-gray-700 text-sm">
          Logged in as: {currentUser} ({userRole})
        </div>
        <Button onClick={() => supabase.auth.signOut()}>Logout</Button>
      </div>

      {isAdmin && (
        <UploadScreen
          users={userList.map((u) => ({ id: u, name: u }))}
          onSubmit={handleUploadSubmit}
        />
      )}

      <div className="max-w-md mx-auto w-full">
        <label className="block mb-1 font-semibold text-gray-800">
          Select Report:
        </label>
        <select
          className="border rounded p-2 w-full"
          onChange={(e) => loadEntries(e.target.value)}
          defaultValue=""
        >
          <option value="" disabled>
            -- Select a Report --
          </option>
          {reportList.map((r) => (
            <option key={r.id} value={r.id}>
              {new Date(r.created_at).toLocaleString()}
            </option>
          ))}
        </select>
      </div>

      {entries.length > 0 ? (
        <>
          {/* Admin: 5 fixed columns. User: 2 fixed columns. */}
          <div className="overflow-x-auto w-full max-w-full">
            <table className="w-full table-auto border border-gray-300 text-sm text-center whitespace-nowrap">
              <thead className="bg-gray-100">
                {isAdmin ? (
                  <tr>
                    <th className="border px-4 py-2">SKU</th>
                    <th className="border px-4 py-2">On Hand</th>
                    <th className="border px-4 py-2">Count</th>
                    <th className="border px-4 py-2">Description</th>
                    <th className="border px-4 py-2">User</th>
                  </tr>
                ) : (
                  <tr>
                    <th className="border px-4 py-2">SKU</th>
                    <th className="border px-4 py-2">Count</th>
                  </tr>
                )}
              </thead>

              <tbody>
  {entries.map((entry, index) =>
    isAdmin ? (
      <tr key={entry.id} className="hover:bg-gray-50">
        <td className="border px-4 py-2 font-semibold text-left">{entry.sku}</td>
        <td className="border px-4 py-2 text-right">{entry.on_hand ?? ""}</td>
        <td className="border px-4 py-2">
          <Input
            ref={(el) => (inputRefs.current[index] = el)}
            type="number"
            value={entry.count ?? ""}
            onChange={(e) => handleInputChange(entry.id, e.target.value, index)}
            onKeyDown={(e) => {
              if (e.key === "Enter") {
                e.preventDefault();
                focusNextEditableInput(index);
              }
            }}
            className={`${getInputClass(entry.count, entry.on_hand)} w-full text-center`}
          />
        </td>
        <td className="border px-4 py-2 text-left">{entry.description ?? ""}</td>
        <td className="border px-4 py-2 text-center">
          {entry.assigned_to ? entry.assigned_to : "—"}
        </td>
      </tr>
    ) : (
      <tr key={entry.id} className="hover:bg-gray-50">
        <td className="border px-4 py-2 font-semibold text-left">{entry.sku}</td>
        <td className="border px-4 py-2">
          {entry.assigned_to === currentUser ? (
            <Input
              ref={(el) => (inputRefs.current[index] = el)}
              type="number"
              value={entry.count ?? ""}
              onChange={(e) => handleInputChange(entry.id, e.target.value, index)}
              onKeyDown={(e) => {
                if (e.key === "Enter") {
                  e.preventDefault();
                  focusNextEditableInput(index);
                }
              }}
              className={`${getInputClass(entry.count, entry.on_hand)} w-full text-center`}
            />
          ) : (
            <span className="block text-gray-600">{entry.count ?? "NA"}</span>
          )}
        </td>
      </tr>
    )
  )}
</tbody>

            </table>
          </div>

          <div className="flex gap-2 mt-4">
            {!isAdmin && (
              <Button onClick={handleDownloadMissingCounts}>Counts Needed</Button>
            )}
            {isAdmin && (
              <>
                <Button onClick={handleDownloadMissingCounts}>Counts Needed</Button>
                <Button onClick={handleGenerateMismatchReport}>Mismatch Report</Button>
              </>
            )}
          </div>
        </>
      ) : (
        <p className="text-gray-500">No report selected or no entries available yet.</p>
      )}
    </div>
  );
}
