import jsPDF from "jspdf";
import "jspdf-autotable";

/**
 * Generate a "Counts Needed" PDF with exactly two columns: SKU | Count
 * @param {Array<{sku: string}>} missingRows - entries lacking count
 */
export const generateMissingCountsPDF = (missingRows) => {
  const doc = new jsPDF({ orientation: "portrait", unit: "pt", format: "letter" });

  // Title
  doc.setFont("helvetica", "normal");
  doc.setFontSize(12);
  doc.text("Counts Needed", 40, 40);

  // Build body rows: [SKU, Count(blank)]
  const body = (missingRows || []).map((row) => [row?.sku || "", "__________"]);

  doc.autoTable({
    startY: 60,
    head: [["SKU", "Count"]],
    body,
    styles: { fontSize: 12, cellPadding: 6 },
    headStyles: { fillColor: [41, 128, 185], textColor: 255 },
    margin: { left: 40, right: 40 },
    tableLineColor: [200, 200, 200],
    tableLineWidth: 0.5,
    theme: "grid",
    columnStyles: {
      0: { cellWidth: "auto" },
      1: { cellWidth: 120 }, // a generous write-in box
    },
    didDrawPage: (data) => {
      // Optional footer page number
      const pageStr = `Page ${doc.internal.getNumberOfPages()}`;
      doc.setFontSize(10);
      doc.text(pageStr, data.settings.margin.left, doc.internal.pageSize.getHeight() - 20);
    },
  });

  doc.save("counts-needed.pdf");
};
