// src/Main.js
import React from "react";
import { createRoot } from "react-dom/client";
import { supabase } from "./supabase";
import InventoryApp from "./App";

function Main() {
  const [session, setSession] = React.useState(null);

  React.useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
    });

    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setSession(session);
    });

    return () => {
      listener?.subscription.unsubscribe();
    };
  }, []);

  if (!session) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="p-6 border rounded shadow w-80">
          <h1 className="text-xl font-bold mb-4">Login</h1>
          <LoginForm />
        </div>
      </div>
    );
  }

  return <InventoryApp session={session.user.user_metadata} />;
}

function LoginForm() {
  const [email, setEmail] = React.useState("");
  const [password, setPassword] = React.useState("");
  const [error, setError] = React.useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    const { error } = await supabase.auth.signInWithPassword({ email, password });
    if (error) setError(error.message);
  };

  return (
    <form onSubmit={handleLogin} className="space-y-4">
      {error && <p className="text-red-500">{error}</p>}
      <input
        type="email"
        placeholder="Email"
        className="border rounded w-full px-3 py-2"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Password"
        className="border rounded w-full px-3 py-2"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <button className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700" type="submit">
        Login
      </button>
    </form>
  );
}

const container = document.getElementById("root");
const root = createRoot(container);
root.render(<Main />);
