import jsPDF from "jspdf";
import "jspdf-autotable";

/**
 * Generate a "Counts Needed" PDF
 * Two SKU+Count pairs per row, clean B/W, empty boxes for Count
 */
export const generateMissingCountsPDF = (missingRows) => {
  const doc = new jsPDF({ orientation: "portrait", unit: "pt", format: "letter" });

  const pageWidth = doc.internal.pageSize.getWidth();
  const pageHeight = doc.internal.pageSize.getHeight();

  // Header
  const addHeader = () => {
    doc.setFont("helvetica", "bold");
    doc.setFontSize(14);
    doc.text("Counts Needed Report", pageWidth / 2, 40, { align: "center" });

    const pageCount = doc.internal.getNumberOfPages();
    const currentPage = doc.internal.getCurrentPageInfo().pageNumber;
    doc.setFont("helvetica", "normal");
    doc.setFontSize(10);
    doc.text(`Page ${currentPage} of ${pageCount}`, pageWidth - 40, 40, { align: "right" });
  };

  // Footer
  const addFooter = () => {
    const pageCount = doc.internal.getNumberOfPages();
    for (let i = 1; i <= pageCount; i++) {
      doc.setPage(i);
      doc.setFontSize(10);
      doc.text(`Page ${i} of ${pageCount}`, pageWidth / 2, pageHeight - 20, { align: "center" });
    }
  };

  // Build rows: [SKU, blank, SKU, blank]
  const rows = [];
  for (let i = 0; i < missingRows.length; i += 2) {
    const leftSKU = missingRows[i]?.sku || "";
    const rightSKU = missingRows[i + 1]?.sku || "";
    rows.push([leftSKU, "", rightSKU, ""]);
  }

  // Add header before first table
  addHeader();

  // Table
  doc.autoTable({
    startY: 60,
    head: [["SKU", "Count", "SKU", "Count"]],
    body: rows,
    styles: {
      fontSize: 10,
      cellPadding: 6,
      valign: "middle",
      halign: "center",
      lineColor: [0, 0, 0],
      lineWidth: 0.2,
    },
    headStyles: {
      fillColor: [255, 255, 255], // white header
      textColor: [0, 0, 0],
      fontStyle: "bold",
      halign: "center",
    },
    columnStyles: {
      0: { cellWidth: 200 },
      1: { cellWidth: 80 },  // empty box
      2: { cellWidth: 200 },
      3: { cellWidth: 80 },  // empty box
    },
    margin: { left: 40, right: 40 },
    tableWidth: "auto",
    didDrawPage: () => {
      addHeader();
    },
  });

  addFooter();
  doc.save("counts-needed.pdf");
};
