import React, { useState, useEffect, useRef } from "react";
import * as XLSX from "xlsx";
import jsPDF from "jspdf";
import "jspdf-autotable";
import { supabase } from "./supabase";

const Input = React.forwardRef((props, ref) => (
  <input
    {...props}
    ref={ref}
    className={`px-2 py-1 rounded border ${props.className || "border-gray-300"}`}
  />
));

const Button = (props) => (
  <button
    {...props}
    className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
  />
);

const InventoryApp = () => {
  const [entries, setEntries] = useState([]);
  const fileInputRef = useRef();

  useEffect(() => {
    fetchEntries();
  }, []);

  const fetchEntries = async () => {
    const { data, error } = await supabase
      .from("entries")
      .select("*")
      .range(0, 9999);

    if (error) {
      console.error("Error fetching entries:", error);
    } else {
      setEntries(data);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = async (e) => {
      const data = new Uint8Array(e.target.result);
      const workbook = XLSX.read(data, { type: "array" });
      const sheet = workbook.Sheets[workbook.SheetNames[0]];
      const parsedData = XLSX.utils.sheet_to_json(sheet);

      for (const row of parsedData) {
        await supabase.from("entries").insert([row]);
      }

      fetchEntries();
    };

    reader.readAsArrayBuffer(file);
  };

  const handleGenerateMismatchReport = () => {
    const mismatched = entries.filter(
      (e) => e.count !== undefined && e.count !== null && e.count !== e.on_hand
    );

    const doc = new jsPDF();
    doc.text("Mismatch Report", 10, 10);
    doc.autoTable({
      head: [["SKU", "On Hand", "Count", "User"]],
      body: mismatched.map((entry) => [
        entry.sku,
        entry.on_hand,
        entry.count,
        entry.user || ""
      ])
    });
    doc.save("mismatch_report.pdf");
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Inventory Report</h1>
      <input type="file" ref={fileInputRef} onChange={handleFileUpload} className="mb-4" />
      <Button onClick={handleGenerateMismatchReport}>Download Mismatch Report</Button>
      <div className="mt-4">
        <table className="w-full table-auto border">
          <thead>
            <tr>
              <th className="border px-2 py-1">SKU</th>
              <th className="border px-2 py-1">On Hand</th>
              <th className="border px-2 py-1">Count</th>
              <th className="border px-2 py-1">User</th>
            </tr>
          </thead>
          <tbody>
            {entries.map((entry, index) => (
              <tr key={index} className="text-center">
                <td className="border px-2 py-1 font-bold">{entry.sku}</td>
                <td className="border px-2 py-1">{entry.on_hand}</td>
                <td className="border px-2 py-1">{entry.count}</td>
                <td className="border px-2 py-1">{entry.user || ""}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default InventoryApp;
